        "constant": false,
        "inputs": [{ "name": "spender", "type": "address" }, {
          "name": "tokens",
          "type": "uint256"
        }],
        "name": "approve",
        "outputs": [{ "name": "success", "type": "bool" }],
        "payable": false,
        "stateMutability": "nonpayable",
        "type": "function"
      }, {
        "constant": true,
