        "inputs": [{ "name": "a", "type": "uint256" }, { "name": "b", "type": "uint256" }],
        "name": "safeDiv",
        "outputs": [{ "name": "c", "type": "uint256" }],
        "payable": false,
        "stateMutability": "pure",
        "type": "function"
      }, {
        "constant": false,
        "inputs": [{ "name": "spender", "type": "address" }, {
          "name": "tokens",
